<?phpdefined('_VALID_NVB') or die('Direct Access to this location is not allowed.');$tpl = new TemplatePower($CONFIG['template_dir'] . "/info_search.htm");$tpl->prepare();langsite();include_once 'manager/dataprovider/search_content.php';$objSearch = new search_content();$keywords = clean_value($_GET['qr']);$tpl->assignGlobal("keywords", $_GET['qr']);$keywords = trim(strtolower($objSearch->RemoveSign($keywords)));$tpl->assignGlobal("h_keywords", $keywords);$db = $objSearch->getListItem($keywords, 1);if (count($db) <= 0)    $tpl->newBlock("no_result");foreach ($db as $rs) {    $tpl->newBlock("item_list");    $tpl->assign("name", $rs['name']);    $tpl->assign("intro", strstrim(strip_tags($rs['intro']), 100));    $tpl->assign("link_detail", $dir_path . '/' . url_process::getUrlCategory($rs['id_category']).$rs['url']);    if ($rs['image'])        $tpl->assign("image", '<div class="divimagenews"><img style="float: left; margin-right:10px;" src="' . $cache_image_path . cropimage(160, 120, $dir_path . '/' . $rs['image']) . '" alt="' . $rs['name'] . '" /></div>');}if ($keywords != '') {    $tpl->newBlock("keywords");    $tpl->newBlock("highlight_list");}$tpl->printToScreen();?>